mapper.select:
SELECT USER_ID
  FROM User
 WHERE USER_ID = #{userId}

SELECT USER_NICK
  FROM User
 WHERE USER_NICK = #{userNick}

SELECT USER_SEQ
  FROM User
 WHERE USER_ID = #{userId}

mapper.update:
UPDATE User


		USER_AUTH_YN = 'Y'


 WHERE USER_SEQ = #{userSeq}
   AND USER_AUTH_KEY = #{userAuthKey}
